// src/components/PostField.js
import React, { useState } from "react";
import "./PostField.css";

const PostField = () => {
  const [postContent, setPostContent] = useState("");

  const handlePostChange = (e) => {
    setPostContent(e.target.value);
  };

  const handlePostSubmit = (e) => {
    e.preventDefault();
    // Lógica para adicionar a postagem (ainda não implementada)
    console.log("Post submitted:", postContent);
    setPostContent(""); // Limpar o campo após a postagem
  };

  return (
    <div className="post-field-container">
      <form onSubmit={handlePostSubmit} className="post-field-form">
        <textarea
          className="post-input"
          placeholder="Escreva algo..."
          value={postContent}
          onChange={handlePostChange}
        />
        <button type="submit" className="post-submit-btn">
          Postar
        </button>
      </form>
    </div>
  );
};

export default PostField;
