// src/components/Register.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  FormControlLabel,
} from "@mui/material";

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    birthDay: "",
    birthMonth: "",
    birthYear: "",
    agreeTerms: false,
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, checked, type } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError("As senhas não coincidem.");
      return;
    }
    if (!formData.agreeTerms) {
      setError("Você deve concordar com os termos e políticas.");
      return;
    }

    setLoading(true);
    setError("");
    setSuccess("");

    try {
      const response = await axios.post("http://localhost:8000/api/register/", {
        username: formData.email,
        email: formData.email,
        password: formData.password,
        first_name: formData.firstName,
        last_name: formData.lastName,
        birth_date: `${formData.birthYear}-${formData.birthMonth}-${formData.birthDay}`,
      });
      console.log("Usuário registrado:", response.data);
      setSuccess(
        "Registro realizado com sucesso! Redirecionando para o login..."
      );
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Erro ao registrar usuário.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm">
      {/* Cabeçalho */}
      <Typography
        variant="h2"
        component="h1"
        align="center"
        sx={{
          fontFamily: "Poppins, sans-serif",
          fontWeight: 600,
          mt: 4,
          mb: 2,
        }}
      >
        trinar
      </Typography>

      {/* Formulário */}
      <Box
        sx={{
          backgroundColor: "background.paper",
          borderRadius: 2,
          boxShadow: 3,
          p: 3,
          mt: 0.5,
        }}
      >
        <Typography variant="h5" component="h2" align="center" gutterBottom>
          Crie uma conta
        </Typography>
        <Typography variant="subtitle1" color="textSecondary" align="center">
          É rápido e fácil.
        </Typography>
        {/* Mensagens de erro e sucesso */}
        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}
        {success && (
          <Alert severity="success" sx={{ mt: 2 }}>
            {success}
          </Alert>
        )}

        {/* Campos do Formulário */}
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          {/* Nome e Sobrenome */}
          <Box sx={{ display: "flex", gap: 2 }}>
            <TextField
              fullWidth
              margin="normal"
              label="Nome"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
            />
            <TextField
              fullWidth
              margin="normal"
              label="Sobrenome"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
            />
          </Box>


          {/* Email */}
          <TextField
            fullWidth
            margin="normal"
            label="Email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          {/* Senha e Confirmar Senha */}
          <TextField
            fullWidth
            margin="normal"
            label="Senha"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <TextField
            fullWidth
            margin="normal"
            label="Confirmar Senha"
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />



          {/* Termos e Políticas */}
          <FormControlLabel
            control={
              <Checkbox
              name="agreeTerms"
              checked={formData.agreeTerms}
              onChange={handleChange}
              required
              />
            }
            label={
              <Typography variant="body2">
                Eu concordo com os{" "}
                <Link
                  to="/terms"
                  style={{
                    textDecoration: "none",
                    color: "primary.main",
                    marginRight: 15,
                  }}
                >
                  Termos de Serviço
                </Link>
                ,{" "}
                <Link
                  to="/privacy"
                  style={{
                    textDecoration: "none",
                    color: "primary.main",
                    marginRight: 15,
                  }}
                >
                  Política de Privacidade
                </Link>{" "}
                e{" "}
                <Link
                  to="/cookies"
                  style={{
                    textDecoration: "none",
                    color: "primary.main",
                    marginRight: 15,
                  }}
                >
                  Política de Cookies
                </Link>
              </Typography>
            }
            sx={{ mt: 2 }}
          />

          {/* Botão de Cadastro */}
          <Button
            fullWidth
            variant="contained"
            type="submit"
            disabled={loading}
            sx={{ mt: 3, mb: 2, background: "#00a400" }}
          >
            {loading ? "Cadastrando..." : "Cadastre-se"}
          </Button>

          {/* Link para o Login */}
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Já tem uma conta?{" "}
            <Link
              to="/login"
              style={{ textDecoration: "none", color: "primary-main" }}
            >
              Faça login
            </Link>
          </Typography>
        </Box>
      </Box>

      {/* Footer */}
      <Box sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="body2" color="textSecondary">
          © 2025 Trinar. Todos os direitos reservados.
        </Typography>
        {/* Futuros links do footer */}
        <Box sx={{ mt: 1 }}>
          <Link
            to="/register"
            style={{
              textDecoration: "none",
              color: "gray",
              marginRight: 15,
            }}
          >
            Registrar
          </Link>
          <Link
            to="/login"
            style={{
              textDecoration: "none",
              color: "gray",
              marginRight: 15,
            }}
          >
            Entrar
          </Link>
          <Link
            to="/about"
            style={{
              textDecoration: "none",
              color: "gray",
              marginRight: 15,
            }}
          >
            Sobre
          </Link>
          <Link
            to="/messenger"
            style={{ 
              textDecoration: "none", 
              color: "gray",
              marginRight: 15, 
            }}
          >
            Messenger
          </Link>
          <Link
            to="/desenvolvedor"
            style={{ 
              textDecoration: "none", 
              color: "gray",
              marginRight: 15, 
            }}
          >
            Desenvolvedores
          </Link>
        </Box>
      </Box>
    </Container>
  );
};

export default Register;
