import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import {
  Container,
  Typography,
  TextField,
  Button,
  Checkbox,
  FormControlLabel,
} from "@mui/material";

const RegisterSchema = Yup.object().shape({
  firstName: Yup.string().required("Nome é obrigatório"),
  lastName: Yup.string().required("Sobrenome é obrigatório"),
  email: Yup.string().email("Email inválido").required("Email é obrigatório"),
  password: Yup.string().min(6, "Senha curta").required("Senha é obrigatória"),
  agreeTerms: Yup.boolean().oneOf([true], "Você deve aceitar os termos."),
});

const Register = () => {
  return (
    <Container maxWidth="sm">
      <Typography variant="h4" align="center" gutterBottom>
        Registro
      </Typography>
      <Formik
        initialValues={{
          firstName: "",
          lastName: "",
          email: "",
          password: "",
          agreeTerms: false,
        }}
        validationSchema={RegisterSchema}
        onSubmit={(values) => {
          console.log("Formulário enviado com sucesso:", values);
        }}
      >
        {({ handleChange, handleSubmit, values }) => (
          <Form onSubmit={handleSubmit} noValidate>
            <TextField
              label="Nome"
              name="firstName"
              value={values.firstName}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <ErrorMessage name="firstName" component="div" />

            <TextField
              label="Sobrenome"
              name="lastName"
              value={values.lastName}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <ErrorMessage name="lastName" component="div" />

            <TextField
              label="Email"
              name="email"
              type="email"
              value={values.email}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <ErrorMessage name="email" component="div" />

            <FormControlLabel
              control={
                <Checkbox
                  name="agreeTerms"
                  checked={values.agreeTerms}
                  onChange={handleChange}
                />
              }
              label="Eu aceito os termos"
            />
            <ErrorMessage name="agreeTerms" component="div" />

            <Button type="submit" variant="contained" fullWidth>
              Enviar
            </Button>
          </Form>
        )}
      </Formik>
    </Container>
  );
};

export default Register;
