import React from "react";
import { useNavigate } from "react-router-dom";
import { Typography, Button, Box, Modal } from "@mui/material";
import { motion } from 'framer-motion';

const EmailConfirmed = () => {
  const navigate = useNavigate();

  // Estilo do modal
  const modalStyle = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    boxShadow: 24,
    p: 4,
    borderRadius: 2,
    textAlign: "center",
  };

  // Estilo do fundo
  const backgroundStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundImage: "url(https://source.unsplash.com/random)", // Imagem de fundo moderna
    backgroundSize: "cover",
    backgroundPosition: "center",
    filter: "blur(5px)", // Desfoque no fundo
    zIndex: -1,
  };

  return (
    <div>
      {/* Fundo moderno e desfocado */}
      <div style={backgroundStyle}></div>

      {/* Modal centralizado */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <Box sx={modalStyle}>
          <Typography variant="h4" id="modal-title" gutterBottom>
            E-mail confirmado com sucesso!
          </Typography>
          <Typography variant="body1" id="modal-description" sx={{ mb: 3 }}>
            Você já pode fazer login na sua conta.
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate("/login")}
          >
            Ir para o Login
          </Button>
        </Box>
      </motion.div>
    </div>
  );
};

export default EmailConfirmed;
