import React, { useState } from "react";
import { styled } from "@mui/system";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  FormControlLabel,
  Fade,
} from "@mui/material";

const StyledContainer = styled("div")(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius,
  boxShadow: theme.shadows[10],
  padding: theme.spacing(4),
  width: "100%",
  maxWidth: "600px",
  textAlign: "center",
  transition: "transform 0.3s ease-in-out",
  "&:hover": {
    transform: "scale(1.02)",
  },
}));

const StyledButton = styled(Button)(({ theme }) => ({
  background: "linear-gradient(45deg, #00a400, #00c853)",
  color: theme.palette.common.white,
  fontWeight: "bold",
  padding: theme.spacing(1.5),
  "&:hover": {
    background: "linear-gradient(45deg, #00c853, #00a400)",
  },
}));

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    birthDay: "",
    birthMonth: "",
    birthYear: "",
    agreeTerms: false,
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, checked, type } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError("As senhas não coincidem.");
      return;
    }
    if (!formData.agreeTerms) {
      setError("Você deve concordar com os termos e políticas.");
      return;
    }

    setLoading(true);
    setError("");
    setSuccess("");

    try {
      // Simulação de uma requisição de registro
      setTimeout(() => {
        setSuccess("Registro realizado com sucesso!");
        setLoading(false);
      }, 2000);
    } catch (err) {
      setError("Erro ao registrar usuário.");
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <StyledContainer>
        <Typography variant="h5" component="h2" gutterBottom>
          Crie uma conta
        </Typography>
        <Typography variant="subtitle1" color="textSecondary" gutterBottom>
          É rápido e fácil.
        </Typography>

        {error && (
          <Fade in={!!error}>
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          </Fade>
        )}
        {success && (
          <Fade in={!!success}>
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          </Fade>
        )}

        <Box component="form" onSubmit={handleSubmit}>
          <Box sx={{ display: "flex", gap: 2, mb: 2 }}>
            <TextField
              fullWidth
              label="Nome"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
            />
            <TextField
              fullWidth
              label="Sobrenome"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
            />
          </Box>

          <Typography variant="subtitle2" sx={{ mb: 1 }}>
            Data de Nascimento
          </Typography>
          <Box sx={{ display: "flex", gap: 2, mb: 2 }}>
            <TextField
              fullWidth
              label="Dia"
              name="birthDay"
              value={formData.birthDay}
              onChange={handleChange}
              type="number"
              inputProps={{ min: 1, max: 31 }}
              required
            />
            <FormControl fullWidth>
              <InputLabel>Mês</InputLabel>
              <Select
                name="birthMonth"
                value={formData.birthMonth}
                onChange={handleChange}
                label="Mês"
                required
              >
                {Array.from({ length: 12 }, (_, i) => (
                  <MenuItem key={i + 1} value={(i + 1).toString().padStart(2, "0")}>
                    {new Date(0, i).toLocaleString("default", { month: "long" })}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              fullWidth
              label="Ano"
              name="birthYear"
              value={formData.birthYear}
              onChange={handleChange}
              type="number"
              inputProps={{ min: 1900, max: new Date().getFullYear() }}
              required
            />
          </Box>

          <TextField
            fullWidth
            label="Email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            sx={{ mb: 2 }}
          />

          <TextField
            fullWidth
            label="Senha"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Confirmar Senha"
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
            sx={{ mb: 2 }}
          />

          <FormControlLabel
            control={
              <Checkbox
                name="agreeTerms"
                checked={formData.agreeTerms}
                onChange={handleChange}
                required
              />
            }
            label={
              <Typography variant="body2">
                Concordo com os{" "}
                <a href="/terms" style={{ color: "#00a400", textDecoration: "none" }}>
                  Termos de Serviço
                </a>
                ,{" "}
                <a href="/privacy" style={{ color: "#00a400", textDecoration: "none" }}>
                  Política de Privacidade
                </a>
                {" e "}
                <a href="/cookies" style={{ color: "#00a400", textDecoration: "none" }}>
                  Política de Cookies
                </a>
                .
              </Typography>
            }
            sx={{ mb: 2 }}
          />

          <StyledButton fullWidth type="submit" disabled={loading}>
            {loading ? "Cadastrando..." : "Cadastre-se"}
          </StyledButton>

          <Typography variant="body2" sx={{ mt: 2 }}>
            Já tem uma conta?{" "}
            <a href="/login" style={{ color: "#00a400", textDecoration: "none" }}>
              Faça login
            </a>
          </Typography>
        </Box>
      </StyledContainer>
    </Container>
  );
};

export default Register;
