import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  FormControlLabel,
} from "@mui/material";

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    birthDay: "",
    birthMonth: "",
    birthYear: "",
    agreeTerms: false,
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const months = [
    { value: "01", label: "Janeiro" },
    { value: "02", label: "Fevereiro" },
    { value: "03", label: "Março" },
    { value: "04", label: "Abril" },
    { value: "05", label: "Maio" },
    { value: "06", label: "Junho" },
    { value: "07", label: "Julho" },
    { value: "08", label: "Agosto" },
    { value: "09", label: "Setembro" },
    { value: "10", label: "Outubro" },
    { value: "11", label: "Novembro" },
    { value: "12", label: "Dezembro" },
  ];

  const handleChange = (e) => {
    const { name, value, checked, type } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError("As senhas não coincidem.");
      return;
    }
    if (!formData.agreeTerms) {
      setError("Você deve concordar com os termos e políticas.");
      return;
    }

    setLoading(true);
    setError("");
    setSuccess("");

    try {
      const response = await axios.post("http://localhost:8000/api/register/", {
        username: formData.email,
        email: formData.email,
        password: formData.password,
        first_name: formData.firstName,
        last_name: formData.lastName,
        birth_date: `${formData.birthYear}-${formData.birthMonth}-${formData.birthDay}`,
      });
      console.log("Usuário registrado:", response.data);
      setSuccess(
        "Registro realizado com sucesso! Redirecionando para o login..."
      );
      setTimeout(() => navigate("/login"), 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Erro ao registrar usuário.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 5 }}>
      <Typography
        variant="h3"
        align="center"
        sx={{ fontWeight: 600, mb: 3, color: "primary.main" }}
      >
        trinar
      </Typography>
      <Box
        sx={{
          p: 4,
          backgroundColor: "background.paper",
          boxShadow: 3,
          borderRadius: 3,
        }}
      >
        <Typography variant="h5" align="center" gutterBottom>
          Crie uma conta
        </Typography>
        <Typography variant="body1" align="center" color="textSecondary">
          É rápido e fácil.
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}
        {success && (
          <Alert severity="success" sx={{ mt: 2 }}>
            {success}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Box sx={{ display: "flex", gap: 2, mt: 2 }}>
            <TextField
              label="Nome"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              fullWidth
              required
            />
            <TextField
              label="Sobrenome"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              fullWidth
              required
            />
          </Box>

          <Typography variant="subtitle2" sx={{ mt: 3 }}>
            Data de Nascimento
          </Typography>
          <Box sx={{ display: "flex", gap: 2 }}>
            <TextField
              label="Dia"
              name="birthDay"
              value={formData.birthDay}
              onChange={handleChange}
              type="number"
              inputProps={{ min: 1, max: 31 }}
              fullWidth
              required
            />

            <FormControl fullWidth>
              <InputLabel id="select-month-label">Mês</InputLabel>
              <Select
                labelId="select-month-label"
                name="birthMonth"
                value={formData.birthMonth}
                onChange={handleChange}
                MenuProps={{
                  disableScrollLock: true,
                }}
              >
                {months.map((month) => (
                  <MenuItem key={month.value} value={month.value}>
                    {month.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              label="Ano"
              name="birthYear"
              value={formData.birthYear}
              onChange={handleChange}
              type="number"
              inputProps={{ min: 1900, max: new Date().getFullYear() }}
              fullWidth
              required
            />
          </Box>

          <TextField
            label="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            type="email"
            fullWidth
            required
            sx={{ mt: 2 }}
          />
          <TextField
            label="Senha"
            name="password"
            value={formData.password}
            onChange={handleChange}
            type="password"
            fullWidth
            required
            sx={{ mt: 2 }}
          />
          <TextField
            label="Confirmar Senha"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            type="password"
            fullWidth
            required
            sx={{ mt: 2 }}
          />

          <FormControlLabel
            control={
              <Checkbox
                name="agreeTerms"
                checked={formData.agreeTerms}
                onChange={handleChange}
              />
            }
            label="Eu concordo com os Termos e Políticas."
            sx={{ mt: 2 }}
          />

          <Button
            variant="contained"
            type="submit"
            fullWidth
            disabled={loading}
            sx={{ mt: 3, mb: 2 }}
          >
            {loading ? "Cadastrando..." : "Cadastre-se"}
          </Button>

          <Typography align="center" variant="body2">
            Já tem uma conta? <Link to="/login">Faça login</Link>
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default Register;
