import React from "react";
import { useForm, Controller } from "react-hook-form";
import {
  Container,
  Typography,
  TextField,
  Button,
  FormControlLabel,
  Checkbox,
  Select,
  MenuItem,
} from "@mui/material";

const Register = () => {
  const {
    handleSubmit,
    control,
    setError,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    if (data.password !== data.confirmPassword) {
      setError("confirmPassword", { message: "As senhas não coincidem" });
      return;
    }
    console.log("Formulário enviado com sucesso:", data);
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" align="center" gutterBottom>
        Registro
      </Typography>
      <form onSubmit={handleSubmit(onSubmit)} noValidate>
        <Controller
          name="firstName"
          control={control}
          defaultValue=""
          rules={{ required: "Nome é obrigatório" }}
          render={({ field }) => (
            <TextField
              {...field}
              label="Nome"
              fullWidth
              margin="normal"
              error={!!errors.firstName}
              helperText={errors.firstName?.message}
            />
          )}
        />

        <Controller
          name="lastName"
          control={control}
          defaultValue=""
          rules={{ required: "Sobrenome é obrigatório" }}
          render={({ field }) => (
            <TextField
              {...field}
              label="Sobrenome"
              fullWidth
              margin="normal"
              error={!!errors.lastName}
              helperText={errors.lastName?.message}
            />
          )}
        />

        <Controller
          name="email"
          control={control}
          defaultValue=""
          rules={{ required: "Email é obrigatório", pattern: /^[^@]+@[^@]+\.[^@]+$/ }}
          render={({ field }) => (
            <TextField
              {...field}
              label="Email"
              type="email"
              fullWidth
              margin="normal"
              error={!!errors.email}
              helperText={errors.email?.message}
            />
          )}
        />

        <Controller
          name="birthMonth"
          control={control}
          defaultValue=""
          render={({ field }) => (
            <Select {...field} fullWidth margin="normal">
              <MenuItem value="">Selecione o mês</MenuItem>
              <MenuItem value="1">Janeiro</MenuItem>
              <MenuItem value="2">Fevereiro</MenuItem>
              {/* Outras opções */}
            </Select>
          )}
        />

        <Controller
          name="agreeTerms"
          control={control}
          defaultValue={false}
          rules={{ required: "Você deve concordar com os termos" }}
          render={({ field }) => (
            <FormControlLabel
              control={
                <Checkbox {...field} checked={field.value} />
              }
              label="Concordo com os Termos"
            />
          )}
        />

        <Button type="submit" variant="contained" fullWidth>
          Enviar
        </Button>
      </form>
    </Container>
  );
};

export default Register;
